        private IConfiguration config;

        public HomeController(IConfiguration configuration)
        {
            config = configuration;
        }
        public IActionResult SendEmail()
        {
            var mail = new MailMessage();
            var smtpServer = new SmtpClient(config["smtpServer"]);
            mail.From = new MailAddress(config["SendGridFrom"]);

            mail.To.Add("to email here");
            smtpServer.Port = Convert.ToInt32(config["sendGridPort"]);
            smtpServer.Credentials = new NetworkCredential(config["sendGridUser"], config["sendGridPassword"]);
            smtpServer.EnableSsl = Convert.ToBoolean(config["EnableSsl"]);

            mail.Subject = "Subject here";
            mail.Body = "";
            mail.IsBodyHtml = true;

            smtpServer.Send(mail);

            return View();
        }
        public async Task<IActionResult> Index()
        {


            var apiKey = config["ApiKey"];
            var client = new SendGridClient(apiKey);
            var attachment = ConvertToBase64();



            var From = new EmailAddress("Ishaf@ISHAF.org", "Attachment Test");
            var Subject = "Attachment";
            var PlainTextContent = "this email should contain an attachement, this is a test for send grid";
            var HtmlContent = GetEmailTemplate();
            var to = new EmailAddress("davidzagi93@gmail.com", "me testing");
            var successMsg = string.Empty;
            var msg = MailHelper.CreateSingleEmail(From, to, Subject, PlainTextContent, HtmlContent);
            msg.AddAttachment("my pics.jpg", ConvertToBase64());
            try
            {
                await client.SendEmailAsync(msg);
                successMsg = "Message sent successfully";
            }
            catch (Exception ex)
            {
                successMsg = ex.Message;
            }

            ViewBag.Message = successMsg;
            return View();
        }

        public static string ConvertToBase64()
        {
            byte[] imageArray = System.IO.File.ReadAllBytes("C:\\Users\\David\\source\\repos\\NewsletterApp\\NewsletterApp\\Controllers\\me.jpg");
            return Convert.ToBase64String(imageArray);

        }
        //code to get the template
        public string GetEmailTemplate()
        {
            string body = string.Empty;
            var path = Path.Combine(Directory.GetCurrentDirectory(), "wwwroot\\", "EmailTemplate.html");
            using (StreamReader reader = new StreamReader(path))
            {
                body = reader.ReadToEnd();
            }
            return body;
        }
